mapper.insert:
select last_insert_id() as id
insert into MERCHANT_INFO (
) values ( #{id, jdbcType=VARCHAR}, #{merchantName, jdbcType=VARCHAR}, #{status, jdbcType=DECIMAL}, #{createDate, jdbcType=TIMESTAMP}, #{operater, jdbcType=VARCHAR}, #{notifyUrl, jdbcType=VARCHAR}, #{returnUrl, jdbcType=VARCHAR}, #{payKey, jdbcType=VARCHAR}, #{productName, jdbcType=VARCHAR}, #{contact, jdbcType=VARCHAR}, #{phone, jdbcType=VARCHAR}, #{mobile, jdbcType=VARCHAR}, #{email, jdbcType=VARCHAR}, #{dayNorm, jdbcType=DECIMAL}, #{monthNorm, jdbcType=DECIMAL}, #{singleNorm, jdbcType=DECIMAL}, #{memo, jdbcType=VARCHAR} )

mapper.select:
select count(*) from  MERCHANT_INFO
where  1=1

	and MERCHANT_NAME =#{merchantName,jdbcType=VARCHAR}

select *
from MERCHANT_INFO where 1=1

	and MERCHANT_NAME =#{merchantName,jdbcType=VARCHAR}


	and STATUS =#{status,jdbcType=VARCHAR}


	and OPERATER =#{operater,jdbcType=VARCHAR}


	and PRODUCT_NAME =#{productName,jdbcType=VARCHAR}


	and CONTACT =#{contact,jdbcType=VARCHAR}


limit  #{startRow},#{pageSize}

mapper.update:
update MERCHANT_INFO set

	ID = #{id}


	,MERCHANT_NAME = #{merchantName}


	,STATUS = #{status}


	,OPERATER = #{operater}


	,NOTIFY_URL = #{notifyUrl}


	,RETURN_URL = #{returnUrl}


	,PAY_KEY = #{payKey}


	,PRODUCT_NAME = #{productName}


	,CONTACT = #{contact}


	,PHONE = #{phone}


	,MOBILE = #{mobile}


	,EMAIL = #{email}


	,DAY_NORM = #{dayNorm}


	,MONTH_NORM = #{monthNorm}


	,SINGLE_NORM = #{singleNorm}


	,MEMO = #{memo}

where id = #{id}
